<?phpnamespace Main\Model;use Zend\Db\TableGateway\TableGateway;use Zend\Session\Container; class FolderTable {     protected $tableGateway;     public function __construct(TableGateway $tableGateway)     {         $this->tableGateway = $tableGateway;     }	 public function saveroot($arr)	{				            $data = array(			 'externalsv_id'			=> $arr['externalsv_id'],             'folder_key'       		=>$arr['folder_key'],            			 'folder_name'      		=>$arr['folder_name'],                'role'   			=>$arr['role'],             'datecreat'    => date('Y-m-d H:i:s'),          );                            $this->tableGateway->insert($data);			return 0;	}	 public function saveuserfolder($arr)	{				            $data = array(			 'externalsv_id'			=> $arr['externalsv_id'],             'folder_key'       		=>$arr['folder_key'],            			 'folder_name'      		=>$arr['folder_name'],                'user_id'   			=>$arr['id'],             'datecreat'    => date('Y-m-d H:i:s'),          );                            $this->tableGateway->insert($data);			return 0;	}	public function getfolder($id)	{		$rowset = $this->tableGateway->select(array('id' => $id));         $row = $rowset->current();         if (!$row) {             return false;         }         return $row;	 	}	public function getfolder_byuser($id)	{		$rowset = $this->tableGateway->select(array('user_id' => $id));         $row = $rowset->current();         if (!$row) {             return false;         }        return $row;	 	}	 } ?>